var NeedItUtils = Class.create();
NeedItUtils.prototype = {
  initialize: function() {
  },
	isDatePast: function(inputDate){
    var today = new GlideDateTime().getLocalDate();
    var dateToTest = new GlideDateTime(inputDate).getLocalDate();

    if (dateToTest.before(today)) {
      return true;
    }
    else {
      return false;
    }
  },
  isDateToday: function(inputDate){
  var today = new GlideDateTime().getLocalDate();
  var dateToTest = new GlideDateTime(inputDate).getLocalDate();

  if (dateToTest == today) {
      return true;
    }
  else {
      return false;
    }
  },
  // populateTargetField: function (response, targetField){
  //   var retrievedValue = response.responseXML.documentElement.getAttribute("answer");
  //   g_form.clearValue(targetField);
  //   g_form.setValue(targetField, retrievedValue);
  // },
  type: 'NeedItUtils'
};
