sys_script_include.script:
function ValidateEmailAddress(inputEmailAddress){
    // Use JavaScript coercion to guarantee inputEmailAddress is a string
    inputEmailAddress = inputEmailAddress + '';
    // Compare inputEmailAddress against the allowed syntax as specified in the regular expression
    // If inputEmailAddress has allowed syntax, return true, else return false
    if(inputEmailAddress.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
      return true;
    }
    else {
     return false;
    }
  }
