sysauto_script.script:
var needitPriority = [1,2,3,4,5];
var needitGR = new GlideRecord('x_58872_needit_needit');
needitGR.addQuery('active', true);
needitGR.query();
while(needitGR.next()){
    var selector = Math.floor(Math.random() * needitPriority.length);
    if (selector > needitPriority.length){
        selector = 0;
    }
    var selectedPriority = needitPriority[selector];
	var previousPriority = needitGR.getValue('priority');
        needitGR.setValue('priority', selectedPriority);
        needitGR.setWorkflow(false);
        needitGR.update();
		gs.info(needitGR.getValue('number') + "'s priority has been changed from " + previousPriority + " to " + needitGR.getValue('priority'));
        selector = 0;
}
