var needitAgg = new GlideAggregate('x_58872_needit_needit');
var aggMessages = 'These NeedIts are due tomorrow and are grouped by Request Type:';
needitAgg.addQuery('active', '=', true);
needitAgg.addQuery('due_date', '>=', gs.daysAgoStart(-1));
needitAgg.addAggregate('COUNT', 'u_request_type');
needitAgg.orderBy('');
needitAgg.setGroup(true);
needitAgg.query();
while(needitAgg.next()){
    var message = '';
    var requestType = needitAgg.u_request_type.getDisplayValue();
    message = 'Request type: ' + requestType + '; Number of NeedIts with this type: ' + needitAgg.getAggregate('COUNT', 'u_request_type');
    aggMessages = aggMessages + '\n' + message;
}
gs.info(aggMessages);
