var needitStates = [13, 14, 15, 16, 3, 8];
var needitGR = new GlideRecord('x_58872_needit_needit');
needitGR.addQuery('active', true);
needitGR.query();
while(needitGR.next()){
    var selector = Math.floor(Math.random() * needitStates.length);
    var selectedState = needitStates[selector];
	var previousState = needitGR.getValue('state');
        needitGR.setValue('state', selectedState);
        needitGR.setWorkflow(false);
        needitGR.update();
		gs.info(needitGR.getValue('number') + "'s state has been changed from " + previousState + " to " + needitGR.getValue('state'));
        selector = 0;
}
